sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate){
	if(isLoading|| newValue === ''){
		return;
	}

	var StateVal = g_form.getValue("state");
	if(newValue === '1' && StateVal === '16'){
	g_form.addErrorMessage("Requests with Critical Priority will be first sent to management. oh and the State field value is : " + StateVal);	
	}else{
		g_form.clearMessages();
	}



}
